DROP DATABASE if exists EKART;
CREATE DATABASE EKART;
USE  EKART;


CREATE TABLE EK_PRODUCT_CATEGORY( 
CATEGORY VARCHAR(50) NOT NULL, 
constraint EK_PROD_CATEGORY_PK primary key ( CATEGORY )
);

CREATE TABLE EK_SELLER(
	EMAIL_ID VARCHAR(50),
	NAME VARCHAR(50) NOT NULL,
	PASSWORD VARCHAR(70) NOT NULL,
	PHONE_NUMBER VARCHAR(10) NOT NULL UNIQUE,
	ADDRESS VARCHAR (500) NOT NULL,
	constraint EK_SELLER_EMAIL_ID_PK primary key ( EMAIL_ID )
);

CREATE TABLE EK_PRODUCT(
	PRODUCT_ID INT auto_increment,
	NAME VARCHAR(500) NOT NULL,
	DESCRIPTION VARCHAR(1000) NOT NULL,
	CATEGORY VARCHAR(200) NOT NULL,
	BRAND VARCHAR(250) NOT NULL,
	PRICE BIGINT NOT NULL,
	DISCOUNT DECIMAL(5,2) NOT NULL,
	QUANTITY SMALLINT NOT NULL,
    SELLER_EMAIL_ID VARCHAR(50),
	constraint EK_PRODUCT_ID_PK primary key ( PRODUCT_ID )
);

CREATE TABLE EK_CUSTOMER(
	EMAIL_ID VARCHAR(50),
	NAME VARCHAR(50) NOT NULL,
	PASSWORD VARCHAR(70) NOT NULL,
	PHONE_NUMBER VARCHAR(10) NOT NULL UNIQUE,
	constraint EK_CUSTOMER_EMAIL_ID_PK primary key ( EMAIL_ID )
);

CREATE TABLE EK_CUSTOMER_ADDRESS (
	ADDRESS_ID INT  NOT NULL auto_increment,
	CONTACT_NUMBER VARCHAR(10) NOT NULL,
	LINE_1 VARCHAR (500) NOT NULL,
	LINE_2 VARCHAR (500),
	CITY VARCHAR(50) NOT NULL,
	STATE VARCHAR(50) NOT NULL,
	PIN VARCHAR(10) NOT NULL,
        CUSTOMER_EMAIL_ID VARCHAR(50),
	constraint EK_ADDRESS_ID_PK primary key ( ADDRESS_ID )
);

CREATE TABLE EK_CUSTOMER_CART (
	CART_ID INT auto_increment,
	PRODUCT_ID INT NOT NULL,
	QUANTITY SMALLINT,
    CUSTOMER_EMAIL_ID VARCHAR(50),
	constraint EK_CART_ID_PK primary key (CART_ID)
);

CREATE TABLE EK_ORDER (
	ORDER_ID BIGINT NOT NULL auto_increment,
	ORDER_NUMBER BIGINT NOT NULL,
	DATE_OF_ORDER DATETIME NOT NULL,
	PRODUCT_ID INT NOT NULL,
	QUANTITY SMALLINT NOT NULL,
	TOTAL_PRICE DECIMAL(12,2) NOT NULL,
	ADDRESS_ID INT NOT NULL,
	ORDER_STATUS VARCHAR(20) NOT NULL,
	PAYMENT_THROUGH VARCHAR(20) NOT NULL, 
	DATE_OF_DELIVERY DATETIME,
    CUSTOMER_EMAIL_ID VARCHAR(50),
	constraint EK_ORDER_ID_PK primary key (ORDER_ID)
);

CREATE TABLE EK_CARD( 
	CARD_ID INT auto_increment,
	CARD_TYPE VARCHAR(11) NOT NULL,
	CARD_NUMBER VARCHAR(16) NOT NULL,
	CVV VARCHAR(70) NOT NULL,
	EXPIRY_DATE DATETIME NOT NULL,
	NAME_ON_CARD VARCHAR(50) NOT NULL,
    CUSTOMER_EMAIL_ID VARCHAR(50),
	constraint EK_CARD_ID_PK primary key ( CARD_ID )
);

CREATE TABLE EK_RECOMMENDED_PRODUCT (
	RECOMMENDATION_ID BIGINT NOT NULL auto_increment,
	PRODUCT_ID INT  NOT NULL,
	RECOMMENDATION_TIMESTAMP DATETIME NOT NULL,
	RECOMMENDATION_STATUS VARCHAR(20) NOT NULL,
	SELLER_EMAIL_ID VARCHAR(50),
	constraint EK_RECMNDTION_ID_PK primary key (RECOMMENDATION_ID)
);

CREATE TABLE EK_DEALS_FOR_TODAY (
	DEAL_ID INT auto_increment,
	PRODUCT_ID INT NOT NULL,
	DEAL_DISCOUNT DECIMAL(4,2) NOT NULL,
	DEAL_STARTS_AT DATETIME NOT NULL,
	DEAL_ENDS_AT DATETIME NOT NULL,
    SELLER_EMAIL_ID VARCHAR(50),
	constraint EK_DEAL_ID_PK primary key (DEAL_ID)
);

CREATE TABLE EK_CUSTOMER_WISHLIST (
	WISHLIST_ID INT auto_increment,
	PRODUCT_ID INT NOT NULL,
	PRODUCT_ADDED_DATE DATETIME NOT NULL,
    CUSTOMER_EMAIL_ID VARCHAR(50),
	constraint EK_WISHLIST_ID_PKs primary key (WISHLIST_ID)
);

CREATE TABLE EXCHANGE_OFFERS(
	OFFER_ID INT auto_increment,
	PRODUCT_ID INT NOT NULL,
	OFFER_DISCOUNT DECIMAL(10,2) NOT NULL,
    SELLER_EMAIL_ID VARCHAR(50),
	constraint EK_OFFER_ID_PK primary key (OFFER_ID)
);

CREATE TABLE EK_PRODUCT_FEEDBACK( 
	FEEDBACK_ID INT auto_increment,
	PRODUCT_ID INT NOT NULL,
	CUSTOMER_EMAIL_ID VARCHAR(50)NOT NULL,
	PRODUCT_SATISFACTION SMALLINT NOT NULL,
	PRODUCT_DSCR_MATCH SMALLINT NOT NULL,
	PRODUCT_RECOMMENDATION SMALLINT NOT NULL,
	constraint EK_PROD_FEEDBACK_PK primary key ( FEEDBACK_ID )
);

CREATE TABLE EK_SELLER_FEEDBACK( 
	FEEDBACK_ID INT auto_increment,
	SELLER_EMAIL_ID VARCHAR(50) NOT NULL,
	CUSTOMER_EMAIL_ID VARCHAR(50) NOT NULL,
	PRODUCT_DELIVERY_SPEED SMALLINT NOT NULL,
	PRODUCT_CONDITION SMALLINT NOT NULL,
	PRODUCT_PACKAGING SMALLINT NOT NULL,
	constraint EK_SLR_FEEDBACK_PK primary key ( FEEDBACK_ID )
);

CREATE TABLE EK_FESTIVALS(
    FESTIVAL_ID INT NOT NULL auto_increment,
    FESTIVAL_NAME VARCHAR(50) NOT NULL,
    FESTIVAL_STARTS_AT DATETIME NOT NULL,
	FESTIVAL_ENDS_AT DATETIME NOT NULL,
    constraint EKart_FESTIVAL_ID_PK primary key (FESTIVAL_ID)
);

CREATE TABLE EK_FESTIVALS_OFFERS (
    FESTIVAL_OFFER_ID Int auto_increment,
    SELLER_EMAIL_ID VARCHAR(50),
	FESTIVAL_ID INT NOT NULL,
	PRODUCT_ID INT NOT NULL,
	FESTIVAL_DISCOUNT DECIMAL(4,2) NOT NULL,
	OFFER_STATUS VARCHAR(8) NOT NULL,
	constraint EKart_FESTIVALS_OFFERS_PK primary key (FESTIVAL_OFFER_ID)
);

CREATE TABLE EK_COMING_SOON_PRODUCT
(
	COMING_SOON_PRODUCT_ID BIGINT,
	ADVANCE_BOOKING_STOCK BIGINT NOT NULL,
	BRAND VARCHAR(64) NOT NULL,
	CATEGORY VARCHAR(64) NOT NULL,
	DESCRIPTION VARCHAR(256) NOT NULL,
	PRE_RELEASE_DISCOUNT SMALLINT NOT NULL,
	PRICE BIGINT NOT NULL,
	PRODUCT_NAME VARCHAR(128) NOT NULL,
	RELEASE_DATE DATETIME NOT NULL,
	RELEASE_STATUS VARCHAR(10) NOT NULL,
	SELLER_EMAIL_ID VARCHAR(50),
	CONSTRAINT EK_COMING_SOON_PRODUCT_ID_PK PRIMARY KEY (COMING_SOON_PRODUCT_ID)
);

CREATE TABLE EK_ADVANCE_BOOKING
(
	BOOKING_ID BIGINT NOT NULL,
	BOOKING_DATE DATETIME NOT NULL,
	BOOKING_QUANTITY BIGINT NOT NULL,
	BOOKING_STATUS VARCHAR(9) NOT NULL,
	COMING_SOON_PRODUCT_ID BIGINT NOT NULL,
	CUSTOMER_EMAIL_ID VARCHAR(50),
	CONSTRAINT EK_ADVANCE_BOOKING_ID_PK PRIMARY KEY (BOOKING_ID)
);



ALTER TABLE EK_PRODUCT ADD CONSTRAINT EK_PRODUCT_CATEGORY_FK FOREIGN KEY ( CATEGORY ) REFERENCES EK_PRODUCT_CATEGORY( CATEGORY );
ALTER TABLE EK_PRODUCT ADD CONSTRAINT EK_SELLER_EMAIL_ID_FK FOREIGN KEY ( SELLER_EMAIL_ID ) REFERENCES EK_SELLER( EMAIL_ID );
ALTER TABLE EK_CUSTOMER_ADDRESS ADD CONSTRAINT EK_CUST_ADRS_EMAIL_FK FOREIGN KEY ( CUSTOMER_EMAIL_ID ) REFERENCES EK_CUSTOMER( EMAIL_ID );

ALTER TABLE EK_ORDER ADD CONSTRAINT EK_ORD_PRODUCT_ID_FK FOREIGN KEY ( PRODUCT_ID ) REFERENCES EK_PRODUCT( PRODUCT_ID );
ALTER TABLE EK_ORDER ADD CONSTRAINT EK_ADDRESS_ID_FK FOREIGN KEY ( ADDRESS_ID ) REFERENCES EK_CUSTOMER_ADDRESS( ADDRESS_ID );
ALTER TABLE EK_ORDER ADD CONSTRAINT EK_ORD_ORDER_STATUS_CHK CHECK (ORDER_STATUS IN ('PLACED', 'CONFIRMED', 'DISPATCHED', 'IN_TRANSIT', 'OUT_FOR_DELIVERY', 'DELIVERED', 'CANCELLED', 'RETURNED'));
ALTER TABLE EK_ORDER ADD CONSTRAINT EK_ORD_PAYMENT_THROUGH_CHK CHECK (PAYMENT_THROUGH IN ('DEBIT_CARD', 'CREDIT_CARD', 'CASH_ON_DELIVERY'));
ALTER TABLE EK_ORDER ADD CONSTRAINT EK_CUST_ORDER_MAPP_EMAIL_ID_FK FOREIGN KEY ( CUSTOMER_EMAIL_ID ) REFERENCES EK_CUSTOMER( EMAIL_ID );

ALTER TABLE EK_CUSTOMER_CART ADD CONSTRAINT EK_CART_PRODUCT_ID_FK FOREIGN KEY ( PRODUCT_ID ) REFERENCES EK_PRODUCT( PRODUCT_ID );
ALTER TABLE EK_CUSTOMER_CART ADD CONSTRAINT EK_CUST_CARD_EMAIL_FK FOREIGN KEY ( CUSTOMER_EMAIL_ID ) REFERENCES EK_CUSTOMER( EMAIL_ID );

ALTER TABLE EK_CARD ADD CONSTRAINT EK_CARD_TYPE_CK CHECK (CARD_TYPE in ('DEBIT_CARD','CREDIT_CARD'));
ALTER TABLE EK_CARD ADD CONSTRAINT EK_CUST_CARD_EMAIL_ID_FK FOREIGN KEY ( CUSTOMER_EMAIL_ID ) REFERENCES EK_CUSTOMER( EMAIL_ID );

ALTER TABLE EK_RECOMMENDED_PRODUCT ADD CONSTRAINT EK_RECMNDTION_PRODUCT_ID_FK FOREIGN KEY ( PRODUCT_ID ) REFERENCES EK_PRODUCT( PRODUCT_ID );
ALTER TABLE EK_RECOMMENDED_PRODUCT ADD CONSTRAINT EK_RECMNDTION_STATUS_CHK CHECK (RECOMMENDATION_STATUS IN ('ACTIVE','INACTIVE'));
ALTER TABLE EK_RECOMMENDED_PRODUCT ADD CONSTRAINT EK_SLLR_PROD_RECMND_EMAIL_FK FOREIGN KEY ( SELLER_EMAIL_ID ) REFERENCES EK_SELLER( EMAIL_ID );

ALTER TABLE EK_DEALS_FOR_TODAY ADD CONSTRAINT EK_DEALS_FOR_TODAY_PROD_ID_FK FOREIGN KEY ( PRODUCT_ID ) REFERENCES EK_PRODUCT( PRODUCT_ID );
ALTER TABLE EK_DEALS_FOR_TODAY ADD CONSTRAINT EK_SELLER_DEALS_EMAIL_FK FOREIGN KEY ( SELLER_EMAIL_ID ) REFERENCES EK_SELLER( EMAIL_ID );


ALTER TABLE EK_CUSTOMER_WISHLIST ADD CONSTRAINT EK_CUSTOMER_WISHLIST_ID_FK FOREIGN KEY ( PRODUCT_ID ) REFERENCES EK_PRODUCT( PRODUCT_ID );
ALTER TABLE EK_CUSTOMER_WISHLIST ADD CONSTRAINT EK_CUST_WSHLST_MAPP_EMAIL_FK FOREIGN KEY ( CUSTOMER_EMAIL_ID ) REFERENCES EK_CUSTOMER( EMAIL_ID );
ALTER TABLE EXCHANGE_OFFERS ADD CONSTRAINT EK_PRODUCT_ID_FK FOREIGN KEY ( PRODUCT_ID ) REFERENCES EK_PRODUCT( PRODUCT_ID );
ALTER TABLE EXCHANGE_OFFERS ADD CONSTRAINT EK_SELLER_ID_FK FOREIGN KEY ( SELLER_EMAIL_ID ) REFERENCES EK_SELLER( EMAIL_ID );
ALTER TABLE EK_PRODUCT_FEEDBACK ADD CONSTRAINT EK_PFEEDBACK_PID_FK FOREIGN KEY ( PRODUCT_ID ) REFERENCES EK_PRODUCT( PRODUCT_ID );
ALTER TABLE EK_PRODUCT_FEEDBACK ADD CONSTRAINT EK_PFEEDBACK_CID_FK FOREIGN KEY ( CUSTOMER_EMAIL_ID ) REFERENCES EK_CUSTOMER( EMAIL_ID );


ALTER TABLE EK_SELLER_FEEDBACK ADD CONSTRAINT EK_SLR_EEDBACK_SID_FK FOREIGN KEY ( SELLER_EMAIL_ID ) REFERENCES EK_SELLER( EMAIL_ID );
ALTER TABLE EK_SELLER_FEEDBACK ADD CONSTRAINT EK_SLR_EEDBACK_CID_FK FOREIGN KEY ( CUSTOMER_EMAIL_ID ) REFERENCES EK_CUSTOMER( EMAIL_ID );

ALTER TABLE EK_Festivals_Offers ADD CONSTRAINT EK_Festivals_Offers_FES_ID_FK FOREIGN KEY (FESTIVAL_ID) REFERENCES EK_FESTIVALS(FESTIVAL_ID);
ALTER TABLE EK_Festivals_Offers ADD CONSTRAINT EK_Festivals_Offers_PROD_ID_FK FOREIGN KEY (PRODUCT_ID) REFERENCES EK_PRODUCT(PRODUCT_ID);
ALTER TABLE EK_Festivals_Offers ADD CONSTRAINT EK_Festivals_Offers_SELL_ID_FK FOREIGN KEY (SELLER_EMAIL_ID ) REFERENCES EK_SELLER(EMAIL_ID);

ALTER TABLE EK_COMING_SOON_PRODUCT ADD CONSTRAINT EK_COMING_SOON_PRODUCT_ID CHECK (COMING_SOON_PRODUCT_ID BETWEEN 1 AND 1999999999);
ALTER TABLE EK_COMING_SOON_PRODUCT ADD CONSTRAINT EK_ADVANCE_BOOKING_STOCK_CHECK CHECK (ADVANCE_BOOKING_STOCK BETWEEN 0 AND 1999999999);
ALTER TABLE EK_COMING_SOON_PRODUCT ADD CONSTRAINT EK_CSP_CATEGORY_FK FOREIGN KEY (CATEGORY) REFERENCES EK_PRODUCT_CATEGORY (CATEGORY);
ALTER TABLE EK_COMING_SOON_PRODUCT ADD CONSTRAINT EK_PRE_RELEASE_DISCOUNT_CHECK CHECK (PRE_RELEASE_DISCOUNT BETWEEN 0 AND 99);
ALTER TABLE EK_COMING_SOON_PRODUCT ADD CONSTRAINT EK_CSP_PRICE_CHECK CHECK (PRICE BETWEEN 1 AND 1999999999);
ALTER TABLE EK_COMING_SOON_PRODUCT ADD CONSTRAINT EK_CSP_RELEASE_STATUS_CHECK CHECK (RELEASE_STATUS IN ('RELEASED', 'UNRELEASED'));

ALTER TABLE EK_ADVANCE_BOOKING ADD CONSTRAINT EK_ADVANCE_BOOKING_ID_CHECK CHECK (BOOKING_ID BETWEEN 1 and 1999999999);
ALTER TABLE EK_ADVANCE_BOOKING ADD CONSTRAINT EK_ADVANCE_BOOKING_QTY_CHECK CHECK (BOOKING_QUANTITY BETWEEN 1 and 1999999999);
ALTER TABLE EK_ADVANCE_BOOKING ADD CONSTRAINT EK_ADVANCE_BOOKING_CSP_ID_FK FOREIGN KEY (COMING_SOON_PRODUCT_ID) REFERENCES EK_COMING_SOON_PRODUCT (COMING_SOON_PRODUCT_ID);
ALTER TABLE EK_ADVANCE_BOOKING ADD CONSTRAINT EK_ADVANCE_BOOKING_STATUS_CHEK CHECK (BOOKING_STATUS IN ('BOOKED', 'CANCELLED'));




